import { Component } from '@angular/core';
import { BooksService } from '../../services/books.service';

@Component({
  selector: 'app-listofbooks',
  templateUrl: './listofbooks.component.html',
  styleUrls: ['./listofbooks.component.css']
})
export class ListofbooksComponent {

  books:any[] = []
  pagination:number=1;
  data: any = this.books;  
  alldata:number =0;

  constructor(private service:BooksService){

  }
  ngOnInit(){

    this.getlist()

}
getlist(){
this.service.getAllBooks().subscribe((data:any)=>{
  this.books=data

}) 
 }

 fetchbooks() {
  this.service.getbooks(this.pagination).subscribe((res: any) => {
    this.data = res.data;
    this.alldata = res.total;
    console.log(res.total);
  });
}
renderPage(event: number) {
  this.pagination = event;
  this.fetchbooks();
}
}
